import { useState, useMemo, useEffect } from 'react';
import { motion } from 'framer-motion';

function Home() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [timeLeft, setTimeLeft] = useState({});

  const audioRef = useMemo(() => new Audio('/birthday-music.mp3'), []);

  useEffect(() => {
    audioRef.loop = true;
    return () => audioRef.pause();
  }, [audioRef]);

  const toggleMusic = () => {
    if (isPlaying) {
      audioRef.pause();
    } else {
      audioRef.play();
    }
    setIsPlaying(!isPlaying);
  };

  useEffect(() => {
    const birthday = new Date('2026-01-02T00:00:00');
    const timer = setInterval(() => {
      const now = new Date();
      const difference = birthday - now;
      setTimeLeft({
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
        seconds: Math.floor((difference / 1000) % 60)
      });
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const particles = useMemo(() => 
    Array.from({ length: 20 }, (_, i) => ({
      id: i,
      x: Math.random() * window.innerWidth,
      y: window.innerHeight + 50,
      duration: Math.random() * 5 + 10,
      type: i % 4 === 0 ? 'balloon' : i % 3 === 0 ? 'star' : i % 2 === 0 ? 'heart' : 'gift'
    })), []
  );

  return (
    <div className="page-container">
      {particles.map(p => (
        <motion.div
          key={p.id}
          className="absolute text-4xl"
          style={{ left: p.x }}
          initial={{ y: p.y, opacity: 0 }}
          animate={{ 
            y: -window.innerHeight - 50, 
            rotate: Math.random() * 360 - 180,
            opacity: [0, 1, 0]
          }}
          transition={{
            duration: p.duration,
            repeat: Infinity,
            ease: "linear",
            delay: Math.random() * 2
          }}
        >
          {p.type === 'balloon' ? 'üéà' : p.type === 'star' ? '‚≠ê' : p.type === 'heart' ? '‚ù§Ô∏è' : 'üéÅ'}
        </motion.div>
      ))}

      <motion.div 
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, type: 'spring' }}
        className="flex flex-col items-center text-center"
      >
        <h1 className="text-5xl font-bold text-pink-600 mb-2">Welcome to Atharva's Birthday Party! üéÇ</h1>
        <p className="text-2xl text-blue-800 mb-4">Born on January 2, 2025 ‚Äì Turning 1 Soon! ü•≥</p>
        <div className="countdown">
          Countdown: {timeLeft.days}d {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s
        </div>
      </motion.div>

      <motion.button
        whileHover={{ scale: 1.2 }}
        whileTap={{ scale: 0.9 }}
        onClick={toggleMusic}
        className="fixed bottom-8 right-8 bg-pink-500 text-white rounded-full p-6 shadow-2xl text-3xl z-50"
      >
        {isPlaying ? 'üîá' : 'üéµ'}
      </motion.button>
    </div>
  );
}

export default Home;
