import { useState, useMemo, useEffect } from 'react';
import { motion } from 'framer-motion';
import Confetti from 'react-confetti';

const defaultImages = [
  '/images/image0.jpg',
  '/images/image1.jpg',
  '/images/image2.jpg',
  '/images/image3.jpg',
  '/images/image4.jpg',
  '/images/image5.jpg',
  '/images/image6.jpg',
  '/images/image7.jpg',
  '/images/image8.jpg',
  '/images/image9.jpg',
  '/images/image10.jpg',
  '/images/image11.jpg'
];

function Gallery() {
  const [openedGifts, setOpenedGifts] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [uploadedVideos, setUploadedVideos] = useState([]);
  const [playingVideo, setPlayingVideo] = useState(null);

  useEffect(() => {
    setUploadedImages(JSON.parse(localStorage.getItem('uploadedImages') || '[]'));
    setUploadedVideos(JSON.parse(localStorage.getItem('uploadedVideos') || '[]'));
  }, []);

  const allImages = [...defaultImages.map(src => process.env.PUBLIC_URL + src), ...uploadedImages];

  const openGift = (index) => {
    if (!openedGifts.includes(index)) {
      setOpenedGifts([...openedGifts, index]);
    }
  };

  const particles = useMemo(() => 
    Array.from({ length: 20 }, (_, i) => ({
      id: i,
      x: Math.random() * window.innerWidth,
      y: window.innerHeight + 50,
      duration: Math.random() * 5 + 10,
      type: i % 4 === 0 ? 'balloon' : i % 3 === 0 ? 'star' : i % 2 === 0 ? 'heart' : 'gift'
    })), []
  );

  return (
    <div className="page-container">
      {particles.map(p => (
        <motion.div
          key={p.id}
          className="absolute text-4xl"
          style={{ left: p.x }}
          initial={{ y: p.y, opacity: 0 }}
          animate={{ 
            y: -window.innerHeight - 50, 
            rotate: Math.random() * 360 - 180,
            opacity: [0, 1, 0]
          }}
          transition={{
            duration: p.duration,
            repeat: Infinity,
            ease: "linear",
            delay: Math.random() * 2
          }}
        >
          {p.type === 'balloon' ? 'üéà' : p.type === 'star' ? '‚≠ê' : p.type === 'heart' ? '‚ù§Ô∏è' : 'üéÅ'}
        </motion.div>
      ))}

      <motion.div className="flex flex-col items-center text-center">
        <h1 className="text-5xl font-bold text-pink-600 mb-8">Atharva's Memory Gallery üéÅ</h1>

        {/* Photos in Gift Boxes */}
        <h2 className="text-3xl text-purple-600 mb-6">Photos (Click Gifts to Open! üéÄ)</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 mb-12">
          {allImages.map((src, index) => (
            <motion.div
              key={index}
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ delay: index * 0.1, type: 'spring', stiffness: 120 }}
              className="relative"
            >
              <div 
                className={`present-box ${openedGifts.includes(index) ? 'open' : ''}`} 
                onClick={() => openGift(index)}
              >
                <div className="present">
                  <div className="img-wrap">
                    <img src={src} alt={`Atharva's Memory ${index + 1}`} />
                  </div>
                </div>
                <div className="side front"></div>
                <div className="side back"></div>
                <div className="side left"></div>
                <div className="side right"></div>
                <div className="side top">
                  <span className="to">
                    <span className="name">Gift {index + 1}</span>
                  </span>
                </div>
                <div className="side bottom"></div>
              </div>
              {!openedGifts.includes(index) && (
                <p className="text-center text-sm text-gray-600 mt-2">Click to Open!</p>
              )}
            </motion.div>
          ))}
        </div>

        {/* Videos Section - FIXED OVERLAY! */}
        {uploadedVideos.length > 0 && (
          <>
            <h2 className="text-3xl text-purple-600 mb-6">Atharva's Cute Videos üé•‚ú®</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl">
              {uploadedVideos.map((src, i) => (
                <motion.div
                  key={i}
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ delay: i * 0.1 }}
                  className="relative rounded-2xl overflow-hidden shadow-2xl bg-black"
                  onClick={() => setPlayingVideo(i)}
                >
                  <video 
                    controls 
                    className="w-full rounded-2xl"
                    onPlay={() => setPlayingVideo(i)}
                  >
                    <source src={src} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>

                  {/* Beautiful Overlay - Only shows when NOT playing */}
                  {playingVideo !== i && (
                    <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-40 pointer-events-none">
                      <motion.div
                        initial={{ scale: 0.8 }}
                        animate={{ scale: 1 }}
                        transition={{ repeat: Infinity, duration: 1.5, ease: "easeInOut" }}
                        className="text-white text-6xl drop-shadow-2xl"
                      >
                        ‚ñ∂Ô∏è
                      </motion.div>
                      <p className="absolute bottom-4 text-white text-xl font-bold drop-shadow-lg">
                        Tap to Play ‚ù§Ô∏è
                      </p>
                    </div>
                  )}
                </motion.div>
              ))}
            </div>
          </>
        )}

        {/* Empty State Message */}
        {uploadedVideos.length === 0 && uploadedImages.length === 0 && (
          <p className="text-xl text-gray-600 mt-12">
            Upload photos & videos from the "Upload Memories" page to see them here! üéà
          </p>
        )}
      </motion.div>

      {openedGifts.length > 0 && <Confetti recycle={false} numberOfPieces={300} />}
    </div>
  );
}

export default Gallery;
